import { createSlice } from "@reduxjs/toolkit";

const initialState={status:"logout"}
const auth=createSlice({
    name:"auth",
    initialState,
    reducers:{
        logOut(){
           return {status: "hide" }
        },
        logIn(){
            return  {status: "show"}
        }
    }
})
export const {logOut,logIn} =auth.actions
export const authReducers=auth.reducer
