import { createSlice } from "@reduxjs/toolkit";

const initialState={
    auth:'loggedOut'
}

const store=createSlice({
    name:"Cart",
    initialState,
    reducers:{
    logOut(state){
     state.auth="logOut"
    },
    logIn(state){
        state.auth="logIn"
    }
    }
})
export const reducers= store.reducer
export const {logIn,logOut} = store.actions
